CREATE TABLE COMBOS (ID BIGINT AUTO_INCREMENT NOT NULL, ADDINGDATE DATETIME, COMBO_AVAILIBILITY VARCHAR(255), COMBO_ITEMS VARCHAR(255), COMBO_NAME VARCHAR(255), COMBO_PRICE VARCHAR(255), SAVING VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE COMPANY (ID BIGINT AUTO_INCREMENT NOT NULL, ADDINGDATE DATETIME, NAME VARCHAR(255), PRODUCTNAME VARCHAR(255), PRODUCTTYPE VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE CUSTOMER (ID BIGINT AUTO_INCREMENT NOT NULL, ADDINGDATE DATETIME, BIRTHDATE DATETIME, EMAIL VARCHAR(255), FIRSTNAME VARCHAR(255), LASTNAME VARCHAR(255), MEMBERREF_CODE VARCHAR(255), PASSWORD VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE MEMBERS (ID BIGINT AUTO_INCREMENT NOT NULL, ADDINGDATE DATETIME, ADMIN_MEMBER TINYINT(1) default 0, BIRTHDATE DATETIME, EMAIL VARCHAR(255), FIRSTNAME VARCHAR(255), LASTNAME VARCHAR(255), PASSWORD VARCHAR(255), REFERANCE_CODE VARCHAR(255), Points BIGINT, PRIMARY KEY (ID))
CREATE TABLE POINTS (ID BIGINT AUTO_INCREMENT NOT NULL, ADDINGDATE DATETIME, EMAIL VARCHAR(255), POINT VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE PRODUCTS (ID BIGINT AUTO_INCREMENT NOT NULL, ADDINGDATE DATETIME, PRODUCT_AVAILIBILITY VARCHAR(255), PRODUCT_NAME VARCHAR(255), PRODUCT_PRICE VARCHAR(255), PRODUCT_TYPE VARCHAR(255), PRODUCT_WEIGHT VARCHAR(255), Company_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE combo_item (combo_id BIGINT NOT NULL, product_id BIGINT NOT NULL, PRIMARY KEY (combo_id, product_id))
CREATE TABLE marketing_tree (customer_id BIGINT NOT NULL, member_id BIGINT NOT NULL, PRIMARY KEY (customer_id, member_id))
CREATE TABLE POINTS_MEMBERS (Points_ID BIGINT NOT NULL, member_ID BIGINT NOT NULL, PRIMARY KEY (Points_ID, member_ID))
ALTER TABLE MEMBERS ADD CONSTRAINT FK_MEMBERS_Points FOREIGN KEY (Points) REFERENCES POINTS (ID)
ALTER TABLE PRODUCTS ADD CONSTRAINT FK_PRODUCTS_Company_ID FOREIGN KEY (Company_ID) REFERENCES COMPANY (ID)
ALTER TABLE combo_item ADD CONSTRAINT FK_combo_item_product_id FOREIGN KEY (product_id) REFERENCES PRODUCTS (ID)
ALTER TABLE combo_item ADD CONSTRAINT FK_combo_item_combo_id FOREIGN KEY (combo_id) REFERENCES COMBOS (ID)
ALTER TABLE marketing_tree ADD CONSTRAINT FK_marketing_tree_customer_id FOREIGN KEY (customer_id) REFERENCES CUSTOMER (ID)
ALTER TABLE marketing_tree ADD CONSTRAINT FK_marketing_tree_member_id FOREIGN KEY (member_id) REFERENCES MEMBERS (ID)
ALTER TABLE POINTS_MEMBERS ADD CONSTRAINT FK_POINTS_MEMBERS_member_ID FOREIGN KEY (member_ID) REFERENCES MEMBERS (ID)
ALTER TABLE POINTS_MEMBERS ADD CONSTRAINT FK_POINTS_MEMBERS_Points_ID FOREIGN KEY (Points_ID) REFERENCES POINTS (ID)
